function startCountdown(e,t){let n=(new Date).getTime()+1e3*e,r=setInterval((function(){let e=(new Date).getTime(),a=n-e,o=String(Math.floor(a%864e5/36e5)).padStart(2,"0"),i=String(Math.floor(a%36e5/6e4)).padStart(2,"0"),l=String(Math.floor(a%6e4/1e3)).padStart(2,"0");document.getElementById(t).innerHTML=`${o}:${i}:${l}`,a<0&&(clearInterval(r),document.getElementById(t).innerHTML="Expired")}),1e3);return r}window.onload=function(){startCountdown(18e3,"time");let e=(new Date).getTime()+18e6,t=setInterval((function(){let n=(new Date).getTime(),r=e-n,a=String(Math.floor(r%864e5/36e5)).padStart(2,"0"),o=String(Math.floor(r%36e5/6e4)).padStart(2,"0"),i=String(Math.floor(r%6e4/1e3)).padStart(2,"0");document.getElementById("time").innerHTML=`${a}:${o}:${i}`,r<0&&(clearInterval(t),document.getElementById("time").innerHTML="Expired")}),1e3)};